import ReactDOM from "react-dom";
import React, { Component } from "react";
import { EditorState, convertToRaw, convertFromRaw, AtomicBlockUtils } from "draft-js";
import Editor, { composeDecorators } from "draft-js-plugins-editor";
import createMentionPlugin, {
  defaultSuggestionsFilter,
} from "draft-js-mention-plugin";
import "./editor-styles.css";
import "draft-js-mention-plugin/lib/plugin.css";
import "draft-js-static-toolbar-plugin/lib/plugin.css";
import "draft-js-image-plugin/lib/plugin.css";
import "draft-js-alignment-plugin/lib/plugin.css";
import "draft-js-focus-plugin/lib/plugin.css";
import createToolbarPlugin, { Separator } from "draft-js-static-toolbar-plugin";
import { draftToMarkdown } from "markdown-draft-js";
import createImagePlugin from "draft-js-image-plugin";
import createAlignmentPlugin from "draft-js-alignment-plugin";
import createFocusPlugin from "draft-js-focus-plugin";
import createResizeablePlugin from "draft-js-resizeable-plugin";
import createBlockDndPlugin from "draft-js-drag-n-drop-plugin";
import createDragNDropUploadPlugin, { readFile } from "@mikeljames/draft-js-drag-n-drop-upload-plugin";
import {
  ItalicButton,
  BoldButton,
  UnderlineButton,
  CodeButton,
  HeadlineOneButton,
  HeadlineTwoButton,
  HeadlineThreeButton,
  UnorderedListButton,
  OrderedListButton,
  BlockquoteButton,
} from "draft-js-buttons";

const mentions = [
  {
    name: "TEST GLOS",
  },
];

const initialState = {
  entityMap: {
    0: {
      type: "IMAGE",
      mutability: "IMMUTABLE",
      data: {
        src: "https://dummyimage.com/600x400/000/fff",
      },
    },
  },
  blocks: [
    {
      key: "9gm3s",
      text: "You can have images in your text field. This is a very rudimentary example, but you can enhance the image plugin with resizing, focus or alignment plugins.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {},
    },
    {
      key: "ov7r",
      text: " ",
      type: "atomic",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [
        {
          offset: 0,
          length: 1,
          key: 0,
        },
      ],
      data: {},
    },
    {
      key: "e23a8",
      text: "See advanced examples further down â€¦",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {},
    },
  ],
};

const focusPlugin = createFocusPlugin();
const resizeablePlugin = createResizeablePlugin();
const blockDndPlugin = createBlockDndPlugin();
const alignmentPlugin = createAlignmentPlugin();
const { AlignmentTool } = alignmentPlugin;

const decorator = composeDecorators(
  resizeablePlugin.decorator,
  alignmentPlugin.decorator,
  focusPlugin.decorator,
  blockDndPlugin.decorator
);

function mockUpload(data, success, failed, progress) {
  function doProgress(percent) {
    progress(percent || 1);
    if (percent === 100) {
      // Start reading the file
      Promise.all(data.files.map(readFile)).then((files) =>
        success(files, { retainSrc: true })
      );
    } else {
      setTimeout(doProgress, 250, (percent || 0) + 10);
    }
  }

  doProgress();
}

class AddImageVideoPopup extends Component {
  state = {
    imageUrl: "",
    videoUrl: "",
  };

  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleInsertImage = () => {
    const { onInsertImage } = this.props;
    const { imageUrl } = this.state;
    onInsertImage(imageUrl);
    this.setState({
      imageUrl: "",
    });
  };

  handleInsertVideo = () => {
    const { onInsertVideo } = this.props;
    const { videoUrl } = this.state;
    onInsertVideo(videoUrl);
    this.setState({
      videoUrl: "",
    });
  };

  render() {
    const { isOpen, onClose } = this.props;
    const { imageUrl, videoUrl } = this.state;

    if (!isOpen) {
      return null;
    }

    return (
      <div className="image-video-popup">
        <div className="image-video-popup-content">
          <div className="image-video-popup-header">
            <span className="image-video-popup-title">Insert Image or Video</span>
            <button className="image-video-popup-close" onClick={onClose}>
              &times;
            </button>
          </div>
          <div className="image-video-popup-body">
            <div className="image-section">
              <input
                type="text"
                name="imageUrl"
                value={imageUrl}
                onChange={this.handleInputChange}
                placeholder="Enter image URL"
              />
              <button className="insert-image-btn" onClick={this.handleInsertImage}>
                Insert Image
              </button>
            </div>
            <div className="video-section">
              <input
                type="text"
                name="videoUrl"
                value={videoUrl}
                onChange={this.handleInputChange}
                placeholder="Enter video URL"
              />
              <button className="insert-video-btn" onClick={this.handleInsertVideo}>
                Insert Video
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default class SimpleMentionEditor extends Component {
  constructor(props) {
    super(props);

    this.toolbarPlugin = createToolbarPlugin();

    this.imagePlugin = createImagePlugin({ decorator });

    this.dragNDropFileUploadPlugin = createDragNDropUploadPlugin({
      handleUpload: mockUpload,
      addImage: this.imagePlugin.addImage,
    });

    this.mentionPlugin = createMentionPlugin({
      mentionTrigger: "\\",
      entityMutability: "MUTABLE",
    });
  }

  state = {
    editorState: EditorState.createWithContent(convertFromRaw(initialState)),
    suggestions: mentions,
    isImageVideoPopupOpen: false,
  };

  onChange = (editorState) => {
    this.setState({
      editorState,
    });
  };

  onSearchChange = ({ value }) => {
    this.setState({
      suggestions: defaultSuggestionsFilter(value, mentions),
    });
  };

  onAddMention = () => {
    // get the mention object selected
  };

  focus = () => {
    this.editor.focus();
  };

  handleInsertImage = (imageUrl) => {
    const { editorState } = this.state;
    const contentState = editorState.getCurrentContent();
    const contentStateWithEntity = contentState.createEntity("IMAGE", "IMMUTABLE", { src: imageUrl });
    const entityKey = contentStateWithEntity.getLastCreatedEntityKey();
    const newEditorState = EditorState.set(editorState, { currentContent: contentStateWithEntity });
    this.setState({
      editorState: AtomicBlockUtils.insertAtomicBlock(newEditorState, entityKey, " "),
      isImageVideoPopupOpen: false,
    });
  };

  handleInsertVideo = (videoUrl) => {
    // Insert video logic here
    console.log("Inserting video: ", videoUrl);
    // Note: Draft.js does not have built-in support for video, so you would need to implement a custom block for handling video insertion.
    // This example demonstrates the concept of inserting videos, but actual implementation will require additional code.
    // You can refer to Draft.js documentation and examples for creating custom blocks.
  };

  toggleImageVideoPopup = () => {
    this.setState((prevState) => ({
      isImageVideoPopupOpen: !prevState.isImageVideoPopupOpen,
    }));
  };

  renderMarkdown = () => {
    const contentState = this.state.editorState.getCurrentContent();
    const foo = convertToRaw(contentState);
    return draftToMarkdown(foo, {
      entityItems: {
        "\\mention": {
          open: function (entity) {
            return `<Glossary name="${entity.data.mention.name}" >`;
          },

          close: function (entity) {
            return "</Glossary>";
          },
        },
      },
    });
  };

  render() {
    const { MentionSuggestions } = this.mentionPlugin;
    const { Toolbar } = this.toolbarPlugin;

    const plugins = [
      this.mentionPlugin,
      this.toolbarPlugin,
      this.dragNDropFileUploadPlugin,
      blockDndPlugin,
      focusPlugin,
      alignmentPlugin,
      resizeablePlugin,
      this.imagePlugin,
    ];

    return (
      <div className="editor-container">
        <div className="toolbar">
          {/* ... (rest of the previous code) */}
        </div>
        <div className="editor" onClick={this.focus}>
          {/* ... (rest of the previous code) */}
          <div className="add-image-icon" onClick={this.toggleImageVideoPopup}>
            +
          </div>
          <AlignmentTool />
        </div>
        <hr />
        <AddImageVideoPopup
          isOpen={this.state.isImageVideoPopupOpen}
          onClose={this.toggleImageVideoPopup}
          onInsertImage={this.handleInsertImage}
          onInsertVideo={this.handleInsertVideo}
        />
      </div>
    );
  }
}

ReactDOM.render(<SimpleMentionEditor />, document.getElementById("root"));
